mapper.update:
update
personal_question
set reference_code=#{code}
where personalq_no=#{personalqNo}

update
personal_answer
set reference_code=#{code}
where personala_no=#{personalaNo}

mapper.select:
select reference_code
from personal_question
where personalq_no=#{personalqNo}

select reference_code
from personal_answer
where personala_no=#{personalaNo}

mapper.update:
update
personal_question
set answer=#{state}
where personalq_no=#{personalqNo}

update product_question
set reference_code=#{code}
where productq_no=#{productqNo}

update
product_question
set answer=#{state}
where productq_no=#{productqNo}

update
product_answer
set reference_code=#{code}
where producta_no=#{productaNo}

mapper.select:
select reference_code
from product_question
where productq_no=#{productqNo}

select reference_code
from product_answer
where producta_no=#{productaNo}

select personalq_no, member_no, title, answer, created_at from personal_question where member_no=#{memberNo} and title LIKE CONCAT('%', #{keyword}, '%')
AND personalq_categorycode LIKE #{category}
AND answer LIKE #{answer}
order by created_at asc
order by created_at desc

select productq_no, member_no, product_no, title, secret, answer, created_at
from
product_question
where product_no=#{productNo}
AND title LIKE CONCAT('%', #{keyword}, '%')

    AND productq_categorycode LIKE #{category}


    AND answer LIKE #{answer}


    order by created_at asc


    order by created_at desc

select productq_no, member_no, product_no, title, secret, answer, created_at
from
product_question
where product_no=#{productNo} and member_no=#{memberNo}
AND title LIKE CONCAT('%', #{keyword}, '%')

    AND productq_categorycode LIKE #{category}


    AND answer LIKE #{answer}


    order by created_at asc


    order by created_at desc

select personalq_no, member_no, title, answer, created_at from personal_question where title LIKE CONCAT('%', #{keyword}, '%')
AND personalq_categorycode LIKE #{category}
AND answer LIKE #{answer}
order by created_at asc
order by created_at desc
