mapper.insert:
INSERT INTO
	mem_delivery_address
(
	  member_no
	, address_name

	, recipient_name


	, postal_code


	, address


	, detail_address


	, recipient_phone


	, request

)
VALUES
(
	  #{ memberNo }
	, #{ addressName }

	, #{ recipientName }


	, #{ postalCode }


	, #{ address }


	, #{ detailAddress }


	, #{ recipientPhone }


	, #{ request }

)

mapper.update:
UPDATE
	mem_delivery_address

		  address_name = #{ addressName }

		, recipient_name = #{ recipientName }


		, postal_code = #{ postalCode }


		, address = #{ address }


		, detail_address = #{ detailAddress }


		, recipient_phone = #{ recipientPhone }


		, request = #{ request }


WHERE address_id = #{ addressId }

mapper.select:
SELECT COUNT(*) FROM payment_point WHERE member_no = #{memberNo}
AND payment_point_change < 0
AND payment_point_change > 0
